 export const ContactItem = ({ name, number, id, onDeleteContact }) => {
  return (
    <>
      <p >{name}</p>
      <p className={css.contactNumber}>{number}</p>
      <button
        className={css.contactBtnDelete}
        type="button"
        onClick={() => {
          onDeleteContact(id);
        }}
      >
        {' '}
        Delete
      </button>
    </>
  );
};
