import styled from 'styled-components';

export const ItemInfo = styled.p`
color: grafit;
font-size:14px;
margin-right: 20px;`

export const Button = styled.button`
padding: 5px 20px;
 width: 90px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: cubic-bezier(0.075, 0.82, 0.165, 1) 3250ms;`
